

#import "JFFloatingMenuView.h"
#import "../Drawzb.h"

@implementation JFFloatingMenuView

- (instancetype)initWithFrame:(CGRect)frame
{
       
    if (self = [super initWithFrame:frame]) {
        self.backgroundColor = [UIColor clearColor];
        NSString *imageBase64 = @"";
        NSData *imageData = [[NSData alloc] initWithBase64EncodedString:imageBase64 options:NSDataBase64DecodingIgnoreUnknownCharacters];
        UIImage *image = [UIImage imageWithData:imageData];
        self.iconImageView.image = image;
    }
    return self;
}

#pragma mark - Event
- (void)didTapIconView
{
    [IFuckYou getInstance].overlayView.isShowMenu = ![IFuckYou getInstance].overlayView.isShowMenu;
    [IFuckYou getInstance].floatingMenuViewTwo.frame = self.frame;
}

@end
